package lecongchanh;

public class Lab3_Bai2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i <= 9; i++)
		{
			for(int j = 1; j <= 10; j++)
			{
				System.out.printf("%d x %d = %d", i, j, i * j);
				System.out.println();
			}
			System.out.println("-----------------------------");
		}
	}

}